import React, { Component } from 'react'
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import MenuIcon from '@material-ui/icons/Menu';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button'
import Paper from '@material-ui/core/Paper'
import Home from '../views/home'
import io from 'socket.io-client';
import home from '../views/home';
import { flexbox } from '@material-ui/system';
const socket = io('http://localhost:8000',{ transports: ['websocket'] });

export default class extends Component {
    state = {
        endpoint: 'http://localhost:8000/',
        name : 'sphe',
        messages: [{name:'coll',
                    message:'hello!',
                    timestamp: new Date().toISOString()

                    }],
        newMessage: ''
    }


    componentDidMount= () =>  {
        socket.on('chat', message => {
          message.key = JSON.stringify(message)
          console.log('##### message', message)
         this.setState((prevState) => {
            let messages = prevState.messages
            console.log('prevState.messages', messages)
             messages.push(message)

            // {
            //   messages: messages
            // }
          })
        })
      }
      componentDidUpdate  = (prevStates) => {

    }
    componentWillUnmount = ()  => {
        this.socket.close()
    }

    handleSubmitName = (name) => {
        this.setState({name:name})
    }
    handleSubmitMasseges = (e) => {
        e.preventDefault()
        socket.emit('chat', {
            name:this.state.name,
            message: this.state.newMessage,
            timestamp: new Date().toISOString()

        })

        this.setState({
            newMessage:''
        })

    }
    render() {
        const {open, name, messages, newMessage} = this.state;

        return (
            <div>
                <table style={{height:'300px',top: 'auto',}}>
                    <tbody>
                 {
                     messages.map(message => 
                         <tr key={message.key}>
                             <td>{message.name}:</td>
                             <td>{message.message}</td>
                             <td>{message.timestamp}</td>
                         </tr>

                 )}
                    </tbody>

                </table>
                <div style={{display:flexbox,bottom:0}}

                    >
                {/* <div style={{width:'50%'}}> */}
                <TextField
                    id="outlined-full-width"
                    label="Chat"
                    style={{ top: 10, left:2, right:2, width:'85%'}}
                    placeholder="write message"
                    fullWidth
                    margin="normal"
                    variant="outlined"
                    value={newMessage}
                    onChange={(e)=>{
                        this.setState({newMessage:e.target.value})
                    }}
                    InputLabelProps={{
                    shrink: true,
                    }}

                >



                </TextField>
                {/* </div> */}

                <Button 
                    style={{width:'12%', top:26, left:4}}
                    size="large"
                    variant="outlined"
                    onClick={this.handleSubmitMasseges}
                    >
                    submit
                </Button>


                </div>

            </div>
        )
    }
}
